import React from "react";
import { Quill } from "react-quill";
import "./App.css"

// Custom Undo button icon component for Quill editor. You can import it directly from 'quill/assets/icons/undo.svg' but I found that a number of loaders do not handle them correctly
const CustomUndo = () => (
    <svg viewBox="0 0 18 18">
        <polygon className="ql-fill ql-stroke" points="6 10 4 12 2 10 6 10" />
        <path
            className="ql-stroke"
            d="M8.09,13.91A4.6,4.6,0,0,0,9,14,5,5,0,1,0,4,9"
        />
    </svg>
);

// Redo button icon component for Quill editor
const CustomRedo = () => (
    <svg viewBox="0 0 18 18">
        <polygon className="ql-fill ql-stroke" points="12 10 14 12 16 10 12 10" />
        <path
            className="ql-stroke"
            d="M9.91,13.91A4.6,4.6,0,0,1,9,14a5,5,0,1,1,5-5"
        />
    </svg>
);

// Undo and redo functions for Custom Toolbar
function undoChange() {
    this.quill.history.undo();
}
function redoChange() {
    this.quill.history.redo();
}

// Add sizes to whitelist and register them
const Size = Quill.import("formats/size");
Size.whitelist = ["extra-small", "small", "medium", "large"];
Quill.register(Size, true);

// Add fonts to whitelist and register them
const Font = Quill.import("formats/font");
Font.whitelist = [
    "arial",
    "comic-sans",
    "courier-new",
    "georgia",
    "helvetica",
    "Inter",
    "lucida"
];
Quill.register(Font, true);


// Modules object for setting up the Quill editor
export const modules = (props) => ({
    toolbar: {
        container: "#" + props,
        handlers: {
            undo: undoChange,
            redo: redoChange
        }
    },
    history: {
        delay: 500,
        maxStack: 100,
        userOnly: true
    }
});

// Formats objects for setting up the Quill editor
export const formats = [
    "header",
    "font",
    "size",
    "bold",
    "italic",
    "underline",
    "align",
    "strike",
    "script",
    "blockquote",
    "background",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
    "video",
    "color",
    "code-block"
];


// Quill Toolbar component
export const QuillToolbar = (props) => {
    return (<>
        {props.toolbarId !== undefined &&
            <div id={props.toolbarId} className="edditorToolbar">
                <span className="ql-formats">
                    <button className="ql-bold" />
                </span>
                <span className="ql-formats">
                    {/* <select className="ql-font">
                        <option value="arial" > Arial </option>
                    </select> */}
                    {/* <select className="ql-size">
                        <option value="extra-small">Extra Small</option>
                        <option value="small">Small</option>
                        <option value="medium" selected>Medium</option>
                        <option value="large">Large</option>
                    </select> */}
                    <select className="ql-header">
                        {/* <option value="1">Heading 1</option> */}
                        {/* <option value="2">Heading 2</option> */}
                        <option value="3">Heading </option>
                        <option value="4">Sub-Heading </option>
                        {/* <option value="5">Heading 5</option> */}
                        {/* <option value="6">Heading 6</option> */}
                        <option value="" selected>Paragraph</option>
                    </select>
                </span>
                <span className="ql-formats">
                    {/* <button className="ql-list" value="ordered" /> */}
                    <button className="ql-list" value="bullet" />
                </span>
                <span className="ql-formats">
                    <button className="ql-list" value="ordered" />
                    {/* <button className="ql-list" value="bullet" /> */}
                </span>
                {/* <span className="ql-formats">
                    <button className="ql-script" value="super" />
                    <button className="ql-script" value="sub" />
                    <button className="ql-blockquote" />
                    <button className="ql-direction" />
                </span> */}
                <span className="ql-formats">
                    {/* <select className="ql-align" /> */}
                    <select className="ql-color" />
                    {/* <select className="ql-background" /> */}
                </span>
                <span className="ql-formats">
                    <button className="ql-link" />
                    <button className="ql-image" />
                    {/* <a><button className="ql-video" /></a> */}
                    {/* <button className="q1-audio" /> */}
                </span>
                {/* <span className="ql-formats">
                    <button className="ql-formula" />
                    <button className="ql-code-block" />
                    <button className="ql-clean" />
                </span> */}
                {/* <span className="ql-formats">
                    <button className="ql-undo">
                        <CustomUndo />
                    </button>
                    <button className="ql-redo">
                        <CustomRedo />
                    </button>
                </span> */}
            </div>
        }
    </>)
}
export default QuillToolbar;







